mapper.insert:

  SELECT LAST_INSERT_ID()

insert into eventnotice ( eventName, ManagetypeID
  )
values (#{eventname,jdbcType=VARCHAR}, #{managetypeid,jdbcType=BIGINT}
  )

mapper.update:
update eventnotice
set eventName = #{eventname,jdbcType=VARCHAR},
  ManagetypeID = #{managetypeid,jdbcType=BIGINT}
where Id = #{id,jdbcType=BIGINT}

mapper.select:
select Id, eventName, ManagetypeID
from eventnotice
where Id = #{id,jdbcType=BIGINT}

select  e.Id, e.eventName, e.ManagetypeID,m.id mid,m.typename mname
from eventnotice e left join managetype m on e.ManagetypeID= m.id
where e.ManagetypeID= #{id}

mapper.insert:
insert into eventnotice ( eventName, ManagetypeID
)
values (#{event}, #{id}
       )
